import { useState } from "react";
import styles from "../asset/css/singup.module.css";
import { useToasts } from "react-toast-notifications";

import { useNavigate } from "react-router-dom";
import { Url } from "../constants/link";

export const Singup = (props) => {
  const [buttonIn, setbuttonIn] = useState(false);
  const [user, setUser] = useState({
    username: "",
    email: "",
    profession: "",
    university: "",
    enrolled: "",
    password: "",
  });

  const history = useNavigate();
  let name, value;

  const handleInputs = (e) => {
    name = e.target.name;
    value = e.target.value;

    setUser({ ...user, [name]: value });
  };

  const { addToast } = useToasts();
  const Postdata = async (e) => {
    setbuttonIn(true);
    e.preventDefault();

    const { username, email, profession, university, enrolled, password } =
      user;
    const response = await fetch(Url + "/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username,
        email,
        profession,
        university,
        enrolled,
        password,
      }),
    });



    const data = await response.json();

    if (response.status === 200) {
      props.setOtpEmail(data.email);

      addToast("Please Verify", {
        appearances: true,
        autoDismiss: true,
      });
      setbuttonIn(false);
      history("/otp");
    } else {
      setbuttonIn(false);
      return addToast("Invalid Information", {
        appearances: false,
        autoDismiss: true,
      });
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.card}>
        <div className={styles.card_title}>
          <h1>Create Account</h1>
        </div>
        <div className={styles.form}>
          <form>
            <input
              type="text"
              name="username"
              id="username"
              placeholder="UserName (Unique)"
              value={user.username}
              onChange={handleInputs}
            />
            <input
              type="email"
              name="email"
              placeholder="University Email (Only)"
              id="email"
              value={user.email}
              onChange={handleInputs}
            />

            <input
              type="text"
              name="profession"
              id="username"
              placeholder="Student or Teacher"
              value={user.profession}
              onChange={handleInputs}
            />
            <input
              type="text"
              name="university"
              id="username"
              placeholder="Univeristy Name"
              value={user.university}
              onChange={handleInputs}
            />
            <input
              type="text"
              name="enrolled"
              id="username"
              placeholder="Department"
              value={user.enrolled}
              onChange={handleInputs}
            />

            <input
              type="password"
              name="password"
              placeholder="Password"
              id="password"
              value={user.password}
              onChange={handleInputs}
            />
            <div className={styles.card_terms}>
              <span>
                Publish a Post Related to Education Content Only (I Agreed)
              </span>
            </div>
            <button onClick={Postdata} disabled={buttonIn}>
              {buttonIn ? "Please Wait.." : "Sing-up"}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Singup;
